namespace Lof\MarketPlace\Block\Widget\Form\Renderer;

use Magento\Framework\Data\Form\Element\AbstractElement;

class Element extends \Magento\Framework\View\Element\Template implements \Magento\Framework\Data\Form\Element\Renderer\RendererInterface
{
    protected $_element;

	protected $_template = 'Lof_MarketPlace::widget/form/renderer/element.phtml';
	
   
    public function getElement()
    {
        return $this->_element;
    }

    public function render(AbstractElement $element)
    {
        $this->_element = $element;
        return $this->toHtml();
    }
}
